var tcsimTeam=require('./tcsim-team-members.json');
var uniqueRandomArray = require('unique-random-array');
var getRandomItem = uniqueRandomArray(tcsimTeam);
module.exports = {
	all: tcsimTeam,
	random: random
};

function random(number){
	if(number === undefined){
		return getRandomItem();
	}else{
		var randomItems = [];
		for(var i=0; i<number; i++)
		{
			randomItems.push(getRandomItem());
		}
		return randomItems;
	}
}