var expect = require('chai').expect;
var tcsimTeam = require('./index');

describe('firebase_app', function() {
  describe('all', function() {
    it('should be an array of strings', function() {
      expect(tcsimTeam.all).to.satisfy(isArrayOfStrings);

      function isArrayOfStrings(array) {
        return array.every(function(item) {
          return typeof item === 'string';
        });
      }
    });

    it('should contain `Aditya Gundechaa`', function() {
      expect(tcsimTeam.all).to.include('Aditya Gundecha');
    });
  });

  describe('random', function() {
    it('should return a random item from the tcsimTeam.all', function() {
      var randomItem = tcsimTeam.random();
      expect(tcsimTeam.all).to.include(randomItem);
    });
	it('should return an array of random items if passed a number',function(){
		var randomItems = tcsimTeam.random(3);
		expect(randomItems).to.have.length(3);
		randomItems.forEach(function(item){
			expect(tcsimTeam.all).to.include(item);
		});
	});
  });
});
